mapper.select:
select  count(1) from (select  a.cszglSum,id,ssmc,ssdd,sskssj,cjkssj,case when zt=0 then '未通过' when zt=1 then '通过' end as zbName  from XT_SSGL left join (select count(1) as cszglSum,ssid from XT_CSZGL group by  ssid) a on XT_SSGL.id=a.ssid) t1 left join (select count(1) as ssjjySum,ssid from XT_SSJJY group by  ssid) b on b.ssid=t1.id 

	and ssmc like '%${ssmc}%'


	and zt = ${zt}

	select t1.*,b.ssjjySum from (select  a.cszglSum,id,ssmc,ssdd,sskssj,cjkssj,ssjssj,cjjssj,case when zt=0 then '未通过' when zt=1 then '通过' end as ztName  from XT_SSGL left join (select count(1) as cszglSum,ssid from XT_CSZGL group by  ssid) a on XT_SSGL.id=a.ssid) t1 left join (select count(1) as ssjjySum,ssid from XT_SSJJY group by  ssid) b on b.ssid=t1.id 

	and ssmc like %${ssmc}%'


	and zt = ${zt}


	order by ${orderByClause}

limit ${startPage}, ${page}
