mapper.select:
SELECT jjy.* ,ry.xm, ry.xb, ry.dh, ry.ly, ry.fzcc, gw.gwmc, sflx.dmmc as sflx, zjlx.dmmc as zjlx, ry.zjhm, group_concat(dm.dmmc) as sbmc FROM XT_SSJJY jjy left join XT_SSGW gw on gw.id = jjy.gwid left join XT_SSJJYSB jjysb on jjysb.ssjjyid = jjy.id left join XT_BSYSB sb on sb.id = jjysb.sbid and sbzt = '1' left join XT_GYDM dm on sb.sblx = dm.id and lbid = '2' left join XT_JJRY ry on ry.id = jjy.ryid and ry.zt = '1' left join XT_GYDM sflx on ry.sflx = sflx.id and sflx.lbid = '3' left join XT_GYDM zjlx on ry.zjlx = zjlx.id and zjlx.lbid = '4' where jjy.zt = '1' and jjy.ssid = #{ssid} group by jjy.id
order by ${orderByClause}
limit ${startPage}, ${page}

SELECT count(1) FROM XT_SSJJY jjy left join XT_SSGW gw on gw.id = jjy.gwid left join XT_SSJJYSB jjysb on jjysb.ssjjyid = jjy.id left join XT_BSYSB sb on sb.id = jjysb.sbid and sbzt = '1' left join XT_GYDM dm on sb.sblx = dm.id and lbid = '2' left join XT_JJRY ry on ry.id = jjy.ryid left join XT_GYDM sflx on ry.sflx = sflx.id and sflx.lbid = '3' left join XT_GYDM zjlx on ry.zjlx = zjlx.id and zjlx.lbid = '4' where jjy.ssid = #{ssid}
and ry.xm like '%${xm}%'
and ry.sflx like '%${sflx}%'

select jjy.* ,ry.xm, ry.xb, ry.dh, ry.ly, ry.fzcc, gw.gwmc, sflx.dmmc as sflx, zjlx.dmmc as zjlx, ry.zjhm, group_concat(dm.dmmc) as sbmc,ry.zt as ryzt FROM XT_SSJJY jjy left join XT_SSGW gw on gw.id = jjy.gwid left join XT_SSJJYSB jjysb on jjysb.ssjjyid = jjy.id left join XT_BSYSB sb on sb.id = jjysb.sbid and sbzt = '1' left join XT_GYDM dm on sb.sblx = dm.id and lbid = '2' left join XT_JJRY ry on ry.id = jjy.ryid left join XT_GYDM sflx on ry.sflx = sflx.id and sflx.lbid = '3' left join XT_GYDM zjlx on ry.zjlx = zjlx.id and zjlx.lbid = '4' where jjy.ssid = #{ssid}
and ry.xm like '%${xm}%'
and ry.sflx like '%${sflx}%'
group by jjy.id
order by ${orderByClause}
limit ${startPage}, ${page}
